var addCommentsBtn = "<button type='button' name='addCommentsBtn' id='addCommentsBtn' class='btn btn-sm btn-info' style='font-size:12px;'>Add Remarks</button>";
//$("#textareaVar001").find("input.form-control").css({"width":"68%"});
$("#textareaVar001").find("div.pmdynaform-field-control").append(addCommentsBtn);

$("#addCommentsBtn").click(function(){

	var remarks = $("#textareaVar001").getValue();

  if(remarks.trim() != ""){

  	var newRemarks = "";
    if($("#linemgrRmks").getValue() == ""){
      newRemarks = remarks.trim();

    }else{
      newRemarks = remarks.trim() +"\n ........................\n"+ $("#linemgrRmks").getValue();
    }
    if($("#approvalRemarks").getValue() == ""){
    	$("#approvalRemarks").setValue(remarks.trim())
    }else{
    	$("#approvalRemarks").setValue(remarks.trim() +"\n ........................\n"+$("#approvalRemarks").getValue());
    }
    $("#linemgrRmks").setValue(newRemarks);
    $("#textareaVar001").setValue("");
    $("#textareaVar001").disableValidation();
    $("#submit0000000001").find("button").removeAttr("disabled");
  }
});
